// Questions.

pub struct Q;

impl day::Day for Q {
  fn number(&self) -> String {
    String::from("1")
  }

  fn a(&self) {
    print!("{}A: ", self.number());
    let result = process_chars_a(INPUT);
    println!("Result = {}", result);
  }

  fn b(&self) {
    print!("{}B: ", self.number());
    let result = process_chars_b(INPUT);
    println!("Result = {}", result.unwrap());
  }
}
