fn process_chars_a(data: &str) -> i32 {
  let mut rv = 0;
  for character in data.chars() {
    match character {
      '(' => rv += 1,
      ')' => rv -= 1,
      _ => panic!("Invalid Character \"{}\"", character)
    }
  }
  rv
}

fn process_chars_b(data: &str) -> Option<usize> {
  let mut rv = 0;
  for (i, character) in data.chars().enumerate() {
    match character {
      '(' => rv += 1,
      ')' => rv -= 1,
      _ => panic!("Invalid Character \"{}\"", character)
    }
    if rv < 0 {
      return Some(i)
    }
  }
  None
}

