// Questions.

pub struct Q;

impl day::Day for Q {
  fn number(&self) -> String {
    String::from("1")
  }

  fn a(&self, use_test_data: bool) {
    print!("{}A: ", self.number());
    let result = if use_test_data {
      process_chars_a(TEST_INPUT)
    } else {
      process_chars_a(INPUT)
    };
    println!("Result = {}", result);
  }

  fn b(&self, use_test_data: bool) {
    print!("{}B: ", self.number());
    let result = if use_test_data {
      process_chars_b(TEST_INPUT)
    } else {
      process_chars_b(INPUT)
    };
    println!("Result = {}", result.unwrap());
  }
}
